import turtle
import random
t=turtle.Turtle()#pen
s=turtle.Screen()#paper

def firework(x,y,clr):
  t.penup()  
  t.goto(x,y)
  t.pendown()
  t.color(clr)
  l=random.randint(20,50)  
  for i in range(5):               
    t.forward(l)
    t.left(144)
 
for i in range():
  turtle.colormode(255)
  x=random.randint(-200,200)
  y=random.randint(-200,200)
  clr=random.randint(0,255),random.randint(0,255),random.randint(0,255)
  firework(x,y,clr)
  
s.mainloop()
